package uz.alovuddin.dagger2.adaptersimport android.view.LayoutInflaterimport android.view.ViewGroupimport androidx.recyclerview.widget.RecyclerViewimport uz.alovuddin.dagger2.Rimport uz.alovuddin.dagger2.databinding.SelectFavoriteItemBindingclass SelectFavoriteAdapter(private val arraylist: ArrayList<String>) :    RecyclerView.Adapter<SelectFavoriteAdapter.ViewH>() {    inner class ViewH(private var binding: SelectFavoriteItemBinding) :        RecyclerView.ViewHolder(binding.root) {        var check = false        fun onBind(str: String) {            binding.tv.text = str            itemView.setOnClickListener {                check = !check                if (!check) {                    binding.tv.setBackgroundResource(R.drawable.ic_item_background)                    binding.tv.setTextColor(binding.root.context.getColor(R.color.text_color))                } else {                    binding.tv.setBackgroundResource(R.drawable.ic_item_selected_background)                    binding.tv.setTextColor(binding.root.context.getColor(R.color.white))                }            }        }    }    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): ViewH {        return ViewH(            SelectFavoriteItemBinding.inflate(                LayoutInflater.from(parent.context),                parent,                false            )        )    }    override fun getItemCount(): Int {        return arraylist.size    }    override fun onBindViewHolder(holder: ViewH, position: Int) {        holder.onBind(arraylist[position])    }}