package uz.alovuddin.dagger2.appimport android.app.Applicationimport uz.alovuddin.dagger2.di.componate.AppComponentimport uz.alovuddin.dagger2.di.componate.DaggerAppComponentimport uz.alovuddin.dagger2.di.modele.DatabaseModuleclass App : Application() {    companion object {        lateinit var appComponent: AppComponent    }    override fun onCreate() {        super.onCreate()        appComponent = DaggerAppComponent.builder().databaseModule(DatabaseModule(this)).build()    }}