package uz.alovuddin.dagger2.repositoryimport kotlinx.coroutines.flow.flowimport uz.alovuddin.dagger2.database.dao.UserDaoimport uz.alovuddin.dagger2.database.entity.UserEntityimport uz.alovuddin.dagger2.network.ApiServiceimport javax.inject.Injectimport javax.inject.Singleton@Singletonclass UserRepository @Inject constructor(private val apiService: ApiService, private val userDao: UserDao) {    suspend fun getUsers() = flow { emit(apiService.getUsers())  }    suspend fun insertDbUsers(list: List<UserEntity>) = userDao.insertAll(list)    suspend fun getDbUsers() = userDao.getUsers()}