package uz.coder.dagger2exampleimport android.app.Applicationimport uz.coder.dagger2example.di.componate.AppComponentimport uz.coder.dagger2example.di.componate.DaggerAppComponentimport uz.coder.dagger2example.di.modele.DatabaseModuleclass App : Application() {    companion object {        lateinit var appComponent: AppComponent    }    override fun onCreate() {        super.onCreate()        appComponent = DaggerAppComponent.builder().databaseModule(DatabaseModule(this)).build()    }}