package uz.coder.dagger2example.adaptersimport android.view.LayoutInflaterimport android.view.ViewGroupimport androidx.recyclerview.widget.RecyclerViewimport androidx.viewpager2.widget.ViewPager2import uz.coder.dagger2example.databinding.SliderItemBindingclass SliderAdapter(private val arraylist: ArrayList<Int>, val viewPager2: ViewPager2) :    RecyclerView.Adapter<SliderAdapter.ViewH>() {    inner class ViewH(var binding: SliderItemBinding) : RecyclerView.ViewHolder(binding.root) {        fun onBind(image: Int) {            binding.image.setImageResource(image)        }    }    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): ViewH {        return ViewH(            SliderItemBinding.inflate(                LayoutInflater.from(parent.context),                parent,                false            )        )    }    override fun getItemCount(): Int {        return arraylist.size    }    override fun onBindViewHolder(holder: ViewH, position: Int) {        holder.onBind(arraylist[position])    }}