package uz.coder.dagger2example.di.modeleimport android.content.Contextimport androidx.room.Roomimport dagger.Moduleimport dagger.Providesimport uz.coder.dagger2example.database.dao.UserDaoimport uz.coder.dagger2example.database.database.AppDatabaseimport javax.inject.Singleton@Moduleclass DatabaseModule(var context: Context) {    @Provides    @Singleton    fun provideContext(): Context = context    @Provides    @Singleton    fun provideAppDatabase(): AppDatabase =        Room.databaseBuilder(context, AppDatabase::class.java, "room")            .allowMainThreadQueries()            .fallbackToDestructiveMigration().allowMainThreadQueries()            .build()    @Provides    @Singleton    fun provideUserDao(appDatabase: AppDatabase):UserDao = appDatabase.userDao()}