package uz.coder.dagger2example.repositoryimport kotlinx.coroutines.flow.flowimport uz.coder.dagger2example.database.dao.UserDaoimport uz.coder.dagger2example.database.entity.UserEntityimport uz.coder.dagger2example.network.ApiServiceimport javax.inject.Injectimport javax.inject.Singleton@Singletonclass UserRepository @Inject constructor(private val apiService: ApiService, private val userDao: UserDao) {    suspend fun getUsers() = flow { emit(apiService.getUsers())  }    suspend fun insertDbUsers(list: List<UserEntity>) = userDao.insertAll(list)    suspend fun getDbUsers() = userDao.getUsers()}