package uz.coder.dagger2example.utilsimport androidx.navigation.NavOptionsimport uz.coder.dagger2example.Robject MyUtils {    fun setAnimation(): NavOptions.Builder {        return NavOptions.Builder()            .setEnterAnim(R.anim.exit_anim)            .setExitAnim(R.anim.pop_enter_anim)            .setPopEnterAnim(R.anim.enter_anim)            .setPopExitAnim(R.anim.pop_exit_anim)    }    fun loadImage():ArrayList<Int>{        val arrayList = ArrayList<Int>()        arrayList.add(R.drawable.splash_image)        arrayList.add(R.drawable.splash_image2)        arrayList.add(R.drawable.splash_image3)        return arrayList    }    fun getFavoriteData():ArrayList<String>{        val arrayList = ArrayList<String>()        arrayList.add("\uD83C\uDFC8   Sports")        arrayList.add("⚖️   Politics")        arrayList.add("\uD83C\uDF1E   Life")        arrayList.add("\uD83C\uDFAE   Gaming")        arrayList.add("\uD83D\uDC3B   Animals")        arrayList.add("\uD83C\uDF34   Nature")        arrayList.add("\uD83C\uDF54   Food")        arrayList.add("\uD83C\uDFA8   Art")        arrayList.add("\uD83D\uDCDC   History")        arrayList.add("\uD83D\uDC57   Fashion")        return arrayList    }}